/**
 * Represents the structure of a trial country with ISO country codes.
 */
export interface TrialCountry {
  /**
   * The two-letter ISO country code.
   */
  code: string;

  /**
   * The three-letter ISO country code.
   */
  code3: CountryCode3;

  /**
   * The full name of the country.
   */
  name: string;

  /**
   * The numeric country code.
   */
  countryNumber: string;
}

